class DataCopy(BaseOEAModule):
    def __init__(self, source_folder, pseudonymize = True):
        BaseOEAModule.__init__(self, source_folder, pseudonymize)
        
    def createFoldersForAll(self):
        print("Copying files from: ", self.stage1np)
        file_list = mssparkutils.fs.ls(self.stage1np)
        for file in file_list:
            # if the filename is preceded by 'dbovw', remove it and use the rest of the file name as the directory
            if file.name.find('dbovw') == 0:
                folder_name = file.name.replace('dbovw', '')
                mssparkutils.fs.mkdirs(self.stage1np + '/' + folder_name)
                print(self.stage1np + '/' + folder_name)


# instantiate the datacopy with a source folder that contains all the ingested data files 
datacopy = DataCopy('testing_folder')

datacopy.createFoldersForAll()
