class DataDictionary(BaseOEAModule):
    def __init__(self, source_folder, pseudonymize = True):
        BaseOEAModule.__init__(self, source_folder, pseudonymize)
        
    def createDictionary(self):
        file_list = mssparkutils.fs.ls(self.stage1np)
        for file in file_list:
            # ignore files
            if file.isFile:
                continue
                
            print(file.path)

dictionary = DataDictionary(source_folder)

dictionary.createDictionary()
