        BaseOEAModule.__init__(self, source_folder, pseudonymize)

      
        self.schemas['EarlyYearsAnswers'] = [
                                    ['Year', 'integer', 'no-op'],
                                    ['Question', 'integer', 'no-op'],
                                    ['Numeracy', 'string', 'no-op'],
                                    ['Literacy', 'string', 'no-op']                    
                                    ]


    def ingest(self):
        oea.ingest_delta_data(self.source_folder, 'EarlyYearsAnswers', self.schemas['EarlyYearsAnswers'], None, 'Question')


