def standardiseString(string, case="upper"):
    """Function to standardise a string to upper or lower case and remove all spaces. Can be used when creating SK's from data """
    if case == "upper":
        return upper(regexp_replace(string, " ", ""))
    
    return lower(regexp_replace(string, " ", ""))

