def transformation(df, name):

    if name == "DMYearLevel_pseudo":
        final_df = DMYearLevel_pseudo(df)
    elif name == "DMCampus_pseudo":
        final_df = DMCampus_pseudo(df)
    elif name == "DMDate_pseudo":
        final_df = DMDate_pseudo(df)
    elif name == "DMStudent_pseudo":
        final_df = DMStudent_pseudo(df)
    elif name == "DMNAPLAN_pseudo":
        final_df = DMNAPLAN_pseudo(df)
    elif name == "DMPersonDemographic_pseudo":
        final_df = DMPersonDemographic_pseudo(df)
    elif name == "DMFactPATResults_pseudo":
        final_df = DMFactPATResults_pseudo(df)  
    elif name == "DMPATTest_pseudo":
        final_df = DMPATTest_pseudo(df)  
    elif name == "EarlyYearsResults_pseudo":
        final_df = EarlyYearsResults_pseudo(df)
    elif name == "EarlyYearsResultsYear2_pseudo":
        final_df = EarlyYearsResultsYear2_pseudo(df)
    elif name == "ATAR_pseudo":
        final_df = ATAR_pseudo(df)
    elif name == "StudentLevel_pseudo":
        final_df = StudentLevel_pseudo(df)
    elif name == "CompassODSStudentDemographicFull_pseudo":
        final_df = CompassODSStudentDemographicFull_pseudo(df)
    elif name == "AEResultsCombined_pseudo":
        final_df = AEResultsCombined(df)
    else:  
        final_df = df

    return final_df

# call this function with the name of the table to transform
def transform(tableName):

    try:
        df = spark.read.load(f"{tableName.path}", format='delta')
        return transformation(df, tableName.name)

    except AnalysisException as e:
        raise ValueError(f"Failed to load {tableName.path}. Check the paths.\nMore info below:\n" + str(e)) 
    
    return None

def saveToTarget(df, target_system, target_path):
    path = f'{target_system}/{target_path}'
    df.write.format('delta').option("overwriteSchema", "true").save(path, header=True, mode="overwrite")

# load data, transform then save from each source folder to the destination 
def stage3Transform(source, destination):
    # source - for each major folder, iterate through each subfolder
    folders = mssparkutils.fs.ls(source)
    for folder in folders:
        #ignore files
        if folder.isFile:
            continue
        # iterate through each subfolder
        subfolders = mssparkutils.fs.ls(folder.path)
        for subfolder in subfolders:
            #There shouldn't be any files at this directory level 
            if subfolder.isFile:
                continue

            # run transformations
            transformed_df = transform(subfolder)
            # if transformation has worked..
            if transformed_df:
                saveToTarget(transformed_df, destination, f'{version}/{subfolder.name}')
            else:
                print(f'Errors in Transform - Skipping: {folder.name}/{subfolder.name}')
